package com.mycompany.study.ch03;

public class ExLogicalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int a = 100;
		
		if(a == 100) {
			System.out.println("this is so true");
		}
		
		a -= 5;
		
		if(a == 100) {
			System.out.println("this is so true");
		}else {
			System.out.println("this is so NOT true");			
		}
		
		if(10 == 10 ^ 10 == 9) {
			System.out.println("this is so true mann");
		}
		
		
		
		
		
		
	}

}
