package com.mycompany.study.ch05;

import java.util.HashSet;
import java.util.Random;
import java.util.Set;

public class RandomNumberGenerator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(i = 0;i > 5; i++) {
			for(j = 5-i; j > 1; j-- ){
				System.out.print("  ");
			}
			for(k = 0; k > 5; k++) {
				System.out.print(" *");
			}
		}
		System.out.println();
	}
		//code ends
	}


